  {%- comment -%} Modal com a tabela {%- endcomment -%}
  <div id="sizeGuideModal" class="size-guide-modal" style="
    display: none;
    position: fixed;
    z-index: 9999;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0,0,0,0.5);
    animation: fadeIn 0.3s;
  ">
    <div class="size-guide-content" style="
      background-color: #fff;
      margin: 5% auto;
      padding: 30px;
      border-radius: 8px;
      width: 90%;
      max-width: 800px;
      max-height: 80vh;
      overflow-y: auto;
      position: relative;
      animation: slideDown 0.3s;
    ">

      {%- comment -%} Bot√£o fechar {%- endcomment -%}
      <button onclick="closeSizeGuide()" style="
        position: absolute;
        right: 20px;
        top: 20px;
        background: none;
        border: none;
        font-size: 28px;
        cursor: pointer;
        color: #666;
        line-height: 1;
      ">&times;</button>

      {%- comment -%} T√≠tulo {%- endcomment -%}
      <h2 style="margin-top: 0; margin-bottom: 20px; font-size: 24px;">
        Guia de Tamanhos
      </h2>

      {%- comment -%} Descri√ß√£o (opcional) {%- endcomment -%}
      {% if product.metafields.custom.tabela_descricao != blank %}
      <p style="color: #666; margin-bottom: 25px; line-height: 1.6;">
        {{ product.metafields.custom.tabela_descricao }}
      </p>
      {% endif %}

      {%- comment -%} Tabela de medidas (HTML ou Imagem) {%- endcomment -%}
      <div style="overflow-x: auto;">
        {% if product.metafields.custom.imagem_tabela_medidas != blank %}
          <img src="{{ product.metafields.custom.imagem_tabela_medidas | image_url: width: 800 }}" 
               alt="Tabela de Medidas" 
               style="max-width: 100%; height: auto; margin: 0 auto; display: block;">
        {% elsif product.metafields.custom.tabela_medidas != blank %}
          {{ product.metafields.custom.tabela_medidas }}
        {% endif %}
      </div>

      {%- comment -%} Dica de medi√ß√£o (opcional) {%- endcomment -%}
      {% if product.metafields.custom.tabela_dica != blank %}
      <div style="
        margin-top: 25px;
        padding: 15px;
        background-color: #f8f8f8;
        border-left: 4px solid #000;
        border-radius: 4px;
      ">
        <strong style="display: block; margin-bottom: 8px;">üí° Dica:</strong>
        {{ product.metafields.custom.tabela_dica }}
      </div>
      {% endif %}

    </div>
  </div>

</div>

{%- comment -%} Estilos e JavaScript {%- endcomment -%}
<style>
  .size-guide-button:hover {
    background-color: #f3f4f6;
    border-color: #9ca3af;
    color: #1f2937;
  }

  @keyframes fadeIn {
    from { opacity: 0; }
    to { opacity: 1; }
  }

  @keyframes slideDown {
    from { 
      transform: translateY(-50px);
      opacity: 0;
    }
    to { 
      transform: translateY(0);
      opacity: 1;
    }
  }

  /* Estilo da tabela dentro do metafield */
  .size-guide-content table {
    width: 100%;
    border-collapse: collapse;
    margin: 20px 0;
  }

  .size-guide-content table th,
  .size-guide-content table td {
    padding: 12px 15px;
    text-align: left;
    border: 1px solid #ddd;
  }

  .size-guide-content table th {
    background-color: #f8f8f8;
    font-weight: 600;
    text-transform: uppercase;
    font-size: 12px;
    letter-spacing: 0.5px;
  }

  .size-guide-content table tr:hover {
    background-color: #f9f9f9;
  }

  @media (max-width: 768px) {
    .size-guide-content {
      margin: 10% auto;
      padding: 20px;
      width: 95%;
    }
  }
</style>

<script>
  function openSizeGuide() {
    document.getElementById('sizeGuideModal').style.display = 'block';
    document.body.style.overflow = 'hidden'; // Previne scroll da p√°gina
  }

  function closeSizeGuide() {
    document.getElementById('sizeGuideModal').style.display = 'none';
    document.body.style.overflow = 'auto'; // Restaura scroll
  }

  // Fechar ao clicar fora do modal
  window.onclick = function(event) {
    const modal = document.getElementById('sizeGuideModal');
    if (event.target == modal) {
      closeSizeGuide();
    }
  }

  // Fechar com tecla ESC
  document.addEventListener('keydown', function(event) {
    if (event.key === 'Escape') {
      closeSizeGuide();
    }
  });
</script>

{% endif %}
